mapper.select:
SELECT order_table.order_id,order_table.description,order_table.process_status,order_table.rating FROM order_table WHERE user_id=#{userId}

    AND process_status=#{processStatus}


    AND (order_id LIKE '%${orderKey}%' OR description LIKE '%${orderKey}%')


    AND created_time >= #{beginTime}


    AND created_time &gt= #{endTime}
